import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { decrement, increment } from "../reducer/reducer";
import { useState } from "react";
import { newUser } from "../reducer/login";

const Main=()=>{

    const [userName,setUserName]=useState()
    const [userAge,setUserAge]=useState()

    const getUserName=(name)=>{
        setUserName(name)
    }

    const getUserAge=(age)=>{
        setUserAge(age)
    }

    const submitUser=(e)=>{
        e.preventDefault()
        dispatch({
            type: 'login/newUser',
            payload:{username:'ignacio'}
        })
    }

    const dispatch=useDispatch()
    const count = useSelector((state)=>{
        console.log(state.counter)
        return state.counter.value
    })

    const incrementCounter=()=>{
        dispatch(increment())
    }

    const reduceCounter=()=>{
        dispatch(decrement())
    }

    return(
        <>
            <div>MAIN</div>
            <div>Contador:{count}</div>
            <button onClick={incrementCounter}>Incrementar</button>
            <button onClick={reduceCounter}>Reducir</button>

            <form>
                <input type="text" placeholder="nombre" onChange={(e)=>getUserName(e.target.value)}></input>
                <input type="number" placeholder="edad" onChange={(e)=>getUserAge(e.target.value)}></input>
                <button onClick={submitUser}>Submit</button>
            </form>
        </>
    )
}

export default Main
