import React from "react";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { changeUser } from "../reducer/login";
import { Link } from "react-router-dom";

const ForgotPassword = ()=>{
    const [userPassword,setUserPassword]=useState()
    const [confirmUserPassword,setConfirmUserPassword]=useState()
    const [passwordWarningStyle,setPasswordWarningStyle]=useState({color:'red',display:'none'})
    const [passwordMatchWarningStyle,setMatchPasswordWarningStyle]=useState({color:'red',display:'none'})

    const dispatch=useDispatch()

    const newPassword=(e)=>{
        e.preventDefault()

        setPasswordWarningStyle({color:'red',display:'none'})
        setMatchPasswordWarningStyle({color:'red',display:'none'})

        const passwordLength=userPassword?.length

        if(passwordLength<8 || passwordLength==null){
            console.log('contrasena corta')
            setPasswordWarningStyle({color:'red',display:'inline'})
        }

        if(userPassword!=confirmUserPassword){
            console.log('contrasena no coincide')
            setMatchPasswordWarningStyle({color:'red',display:'inline'})
        }

        if(userPassword===confirmUserPassword){
            dispatch(changeUser({
                userpassword:userPassword
            }))    
        }

    }

    return(
        <>
            <div>Create new password</div>
            <form>
                <label>New password</label>
                <input type="password" onChange={(e)=>setUserPassword(e.target.value)} required />

                <label>Confirm new password</label>
                <input type="password" onChange={(e)=>setConfirmUserPassword(e.target.value)} required />
                <p style={passwordWarningStyle}>Password needs to be 8 characters or more</p>
                <p style={passwordMatchWarningStyle}>Passwords do not match</p>

                <Link to='/Main'>
                    <button onClick={newPassword}>Change password</button>
                </Link>
            </form>
        </>
    )
}

export default ForgotPassword
