import React from "react";
import { Link } from "react-router-dom";
import { useState } from "react";
import { useDispatch } from "react-redux";
import {newUser} from "../reducer/login";

const SignUp=()=>{

    const [userName,setUserName]=useState()
    const [userAge,setUserAge]=useState()
    const [userMail,setUserMail]=useState()
    const [confirmUserMail,setConfirmUserMail]=useState()
    const [userPassword,setUserPassword]=useState()
    const [confirmUserPassword,setConfirmUserPassword]=useState()
    const [confirmSignUp,setConfirmSignUp]=useState()

    const dispatch=useDispatch()

    const createNewUser=(e)=>{
        e.preventDefault()

        const passwordLength=userPassword?.length

        if(passwordLength<8 || passwordLength==null){
            console.log('contrasena es corta')
        }

        if(userPassword!=confirmUserPassword){
            console.log('contrasena no coincide')
        }

        if(userMail!=confirmUserMail){
            console.log('mail no coincide')
        }

        dispatch(newUser({
            username:userName,
            userage:userAge,
            usermail:userMail,
            userpassword:userPassword
        }))

    }

    return(
        <>
        {!confirmSignUp && (
            <div>
                <div>Sign Up</div>
                <form>

                    <label>Name</label>
                    <input type="text" placeholder="Enter your name..." onChange={(e)=>setUserName(e.target.value)} required/>

                    <label>Age</label>
                    <input type="number" placeholder="Enter your age..." onChange={(e)=>setUserAge(e.target.value)} required/>

                    <label>Email</label>
                    <input type="email" placeholder="Enter your email..." onChange={(e)=>setUserMail(e.target.value)} required/>

                    <label>Confirm email</label>
                    <input type="email" placeholder="Enter your email..." onChange={(e)=>setConfirmUserMail(e.target.value)} required/>

                    <label>Password</label>
                    <input type="password" placeholder="Enter your password..." minLength="8" onChange={(e)=>setUserPassword(e.target.value)} required/>
                    <p>Password needs to be 8 characters or more</p>
                    <p>Password do not match</p>

                    <p>You need to complete all the information in order to sign up</p>

                    <label>Confirm password</label>
                    <input type="password" placeholder="Enter your password..." minLength="8" onChange={(e)=>setConfirmUserPassword(e.target.value)} required/>


                    <button onClick={createNewUser}>Sign Up</button>
                </form>
                <div>
                    <Link to='/'>
                        <p>Already have an account? click here to Log in...</p>
                    </Link>
                </div>
            </div>
        )}
        {confirmSignUp && (
            <div>
                <div>WELCOME!</div>
                <div>We have send you instruction to confirm your register at your email! </div>
                <Link to='/Login'>
                    <button>Log In</button>
                </Link>
            </div>
        )}

        </>
    )
}

export default SignUp
