import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { newUser } from "../reducer/login";

const Login=()=>{

    const dispatch=useDispatch()
    const navigate = useNavigate(); // Hook to access navigation history

    const correctName='Ignacio'
    const correctAge=21
    const correctEmail='ignacio@gmail.com'
    const correctPassword='12345'

    const [userMail,setUserMail]=useState()
    const [userPassword,setUserPassword]=useState()
    const [emailWarningStyle,setEmailWarningStyle]=useState({color:'red',display:'none'})
    const [passwordWarningStyle,setPasswordWarningStyle]=useState({color:'red',display:'none'})

    const submitLogin=(e)=>{
        e.preventDefault()

        setEmailWarningStyle({color:'red',display:'none'})
        setPasswordWarningStyle({color:'red',display:'none'})

        if(userPassword!=correctPassword){
            setPasswordWarningStyle({color:'red',display:'inline'})
        }

        if(userMail!=correctEmail){
            setEmailWarningStyle({color:'red',display:'inline'})
        }

        if(userPassword===correctPassword && userMail===correctEmail){
            console.log('usuario y contrasena correctos')
            dispatch(newUser({
                username:correctName,
                userage:correctAge,
                usermail:correctEmail,
                userpassword:correctPassword
            }))

            navigate('/Main') // redirect the user to '/Main'
        }

    }

    return(
        <>
            <div>LOG IN</div>
            <form>
                <label>Mail</label>
                <input type="email" placeholder="Enter email..." onChange={(e)=>setUserMail(e.target.value)} required/>
                <p style={emailWarningStyle}>Email is not registered</p>
                <label>Password</label>
                <input type="password" placeholder="Enter password..." onChange={(e)=>setUserPassword(e.target.value)} required/>
                <p style={passwordWarningStyle}>Password is incorrect</p>
                <Link to='/Main'>
                    <button onClick={submitLogin}>Log In</button>
                </Link>
            </form>
            <div>
                <Link to='/Forgotpassword'>
                    <p>Forgot password? create new password here...</p>
                </Link>
                <Link to='/Signup'>
                    <p>Don't have an account? Sign Up here...</p>
                </Link>
            </div>
        </>
    )
}

export default Login
