import React from "react";
import { useState } from "react";
import { useDispatch,useSelector } from "react-redux";
import { changeUser } from "../reducer/login";
import { Link } from "react-router-dom";

const ChangeUserData=()=>{

    const [newUserName,setNewUserName]=useState()
    const [newUserAge,setNewUserAge]=useState()
    const [newUserMail,setNewUserMail]=useState()
    const [newConfirmUserMail,setNewConfirmUserMail]=useState()
    const [newUserPassword,setNewUserPassword]=useState()
    const [newConfirmUserPassword,setNewConfirmUserPassword]=useState()

    //Only shows the page if the user is valid to avoid user to enter without Loging in first
    const [validUser,setValidUser]=useState(false)

    //This states are use in the style of the warnings, that will show up when user leaves empty inputs or information is wrong
    const [emailWarningStyle,setEmailWarningStyle]=useState(false)
    const [emailMatchWarningStyle,setEmailMatchWarningStyle]=useState(false)
    const [passwordWarningStyle,setPasswordWarningStyle]=useState(false)
    const [passwordMatchWarningStyle,setPasswordMatchWarningStyle]=useState(false)
    const [inputEmpty,setInputEmpty]=useState(false)

    const dispatch=useDispatch()

    //I bring the data from the Redux store to show the user his current data
    const userName=useSelector((state)=>{
        return state.login.username
    })
    const userAge=useSelector((state)=>{
        return state.login.userage
    })
    const userMail=useSelector((state)=>{
        return state.login.usermail
    })
    const userPassword=useSelector((state)=>{
        return state.login.userpassword
    })

    //verifies that only login users enter the page
    if(!userName==='' && !userAge===0 && !userMail==='' && !userPassword===''){
        setValidUser(true)
    }

    //Verifies the user inputs, and only changes the user data if everything is OK
    const changeUserData=(e)=>{
        e.preventDefault()

        setEmailWarningStyle(false)
        setEmailMatchWarningStyle(false)
        setPasswordMatchWarningStyle(false)
        setPasswordWarningStyle(false)

        const passwordLength=newUserPassword?.length

        if(passwordLength<8 || passwordLength==null){
            console.log('contrasena es corta')
            setPasswordWarningStyle(true)
        }

        if(newUserPassword!=newConfirmUserPassword){
            console.log('contrasena no coincide')
            setPasswordMatchWarningStyle(true)
        }

        if(newUserMail!=newConfirmUserMail){
            console.log('mail no coincide')
            setEmailMatchWarningStyle(true)
        }

        if(!newUserMail?.includes('@') || !newConfirmUserMail?.includes('@')){
            console.log('mail no tiene @')
            setEmailWarningStyle(true)
        }

        if(!newUserName || !newConfirmUserMail || !newUserAge || !newUserPassword || !newConfirmUserMail || !newConfirmUserPassword){
            setInputEmpty(true)
        }else{
            setInputEmpty(false)
        }

        if(passwordLength>=8 && newUserPassword===newConfirmUserPassword && newUserMail && newUserMail===newConfirmUserMail && newUserAge && newUserName){
            dispatch(changeUser({
                username:newUserName,
                userage:newUserAge,
                usermail:newUserMail,
                userpassword:newUserPassword
            }))
        }

    }

    return(
        <>
            {/* {validUser && ( */}
                <div>
                    <div>Change your information</div>
                    <form>

                        <label>Name</label>
                        <input type="text" placeholder="Enter your name..." value={userName} onChange={(e)=>setNewUserName(e.target.value)} required/>

                        <label>Age</label>
                        <input type="number" placeholder="Enter your age..." value={userAge} onChange={(e)=>setNewUserAge(e.target.value)} required/>

                        <label>Email</label>
                        <input type="email" placeholder="Enter your email..." value={userMail} onChange={(e)=>setNewUserMail(e.target.value)} required/>

                        <label>Confirm email</label>
                        <input type="email" placeholder="Enter your email..." onChange={(e)=>setNewConfirmUserMail(e.target.value)} required/>
                        {emailMatchWarningStyle && <p style={{color:'red'}}>Email do not match</p>}
                        {emailWarningStyle && <p style={{color:'red'}}>Email needs to have a @</p>}

                        <label>Password</label>
                        <input type="password" placeholder="Enter your password..." minLength="8" onChange={(e)=>setNewUserPassword(e.target.value)} required/>

                        <label>Confirm password</label>
                        <input type="password" placeholder="Enter your password..." minLength="8" onChange={(e)=>setNewConfirmUserPassword(e.target.value)} required/>
                        {passwordWarningStyle && <p style={{color:'red'}}>Password needs to be 8 characters or more</p>}
                        {passwordMatchWarningStyle && <p style={{color:'red'}}>Password do not match</p>}

                        {inputEmpty && <p style={{color:'red'}}>You need to complete all the information in order to make changes</p>}

                        <button type="submit" onClick={changeUserData}>Confirm changes</button>
                        <Link to='/Main'>
                            <button>Cancel</button>
                        </Link>
                    </form>
                </div>
            {/* )} */}
        </>
    )
}

export default ChangeUserData
