import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";

const ForgotPassword = ()=>{

    const correctMail='ignacio@gmail.com'

    const [userEmail,setUserEmail]=useState()
    const [emaildWarningStyle,setEmailWarningStyle]=useState({color:'red',display:'none'})

    const submitRequest=()=>{
        if(userEmail!=correctMail){
            console.log('mail no registrado')
            setEmailWarningStyle({color:'red',display:'inline'})
        }
    }

    return(
        <>
            <div>Change your password</div>
            <form>
                <label>Enter your mail</label>
                <input type="email" placeholder="Enter your email..." onChange={(e)=>setUserEmail(e.target.value)} required/>
                <p>This email is not registered</p>

                <button onClick={submitRequest}>Submit request</button>
            </form>
        </>
    )
}

export default ForgotPassword
