import { createSlice } from "@reduxjs/toolkit";

export const loginSlice = createSlice({
    name:'userData',
    initialState:{
        username:'',
        userage:0,
        usermail:'',
        userpassword:''
    },
    reducers:{
        newUser: (state,action)=>{
            state.username = action.payload.username
            state.userage=action.payload.userage
        },
        changeUserName: (state,action)=>{
            state.username=action.payload.username,
            state.username=action.payload.usermail,
            state.username=action.payload.userage,
            state.username=action.payload.userpassword
        },
        deleteUser: (state)=>{

        }
    }
})

export const {newUser,changeUser,deleteUser}=loginSlice.actions

export default loginSlice.reducer
