import React from "react";
import { useState } from "react";
import { useDispatch,useSelector } from "react-redux";
import { changeUser } from "../reducer/login";
import { Link } from "react-router-dom";

const ChangeUserData=()=>{

    const [newUserName,setNewUserName]=useState()
    const [newUserAge,setNewUserAge]=useState()
    const [newUserMail,setNewUserMail]=useState()
    const [newConfirmUserMail,setNewConfirmUserMail]=useState()
    const [newUserPassword,setNewUserPassword]=useState()
    const [newConfirmUserPassword,setNewConfirmUserPassword]=useState()

    const [emaildWarningStyle,setEmailWarningStyle]=useState({color:'red',display:'none'})
    const [passwordWarningStyle,setPasswordWarningStyle]=useState({color:'red',display:'none'})

    const dispatch=useDispatch()
    const userName=useSelector((state)=>{
        return state.login.username
    })
    const userAge=useSelector((state)=>{
        return state.login.userage
    })
    const userMail=useSelector((state)=>{
        return state.login.usermail
    })

    const changeUserData=(e)=>{
        e.preventDefault()

        const [emaildWarningStyle,setEmailWarningStyle]=useState({color:'red',display:'none'})

        const passwordLength=newUserPassword?.length

        if(passwordLength<8 || passwordLength==null){
            console.log('contrasena es corta')
        }

        if(newUserPassword!=newConfirmUserPassword){
            console.log('contrasena no coincide')
        }

        if(newUserMail!=newConfirmUserMail){
            console.log('mail no coincide')
            setEmailWarningStyle({color:'red',display:'inline'})
        }

        dispatch(changeUser({
            username:newUserName,
            userage:newUserAge,
            usermail:newUserMail,
            userpassword:newUserPassword
        }))

    }

    return(
        <>
            <div>Change your information</div>
            <form>

                <label>Name</label>
                <input type="text" placeholder="Enter your name..." onChange={(e)=>setNewUserName(e.target.value)} required/>

                <label>Age</label>
                <input type="number" placeholder="Enter your age..." onChange={(e)=>setNewUserAge(e.target.value)} required/>

                <label>Email</label>
                <input type="email" placeholder="Enter your email..." onChange={(e)=>setNewUserMail(e.target.value)} required/>

                <label>Confirm email</label>
                <input type="email" placeholder="Enter your email..." onChange={(e)=>setNewConfirmUserMail(e.target.value)} required/>
                <p>Email do not match</p>

                <label>Password</label>
                <input type="password" placeholder="Enter your password..." minLength="8" onChange={(e)=>setNewUserPassword(e.target.value)} required/>
                <p style={emaildWarningStyle}>Password needs to be 8 characters or more</p>

                <label>Confirm password</label>
                <input type="password" placeholder="Enter your password..." minLength="8" onChange={(e)=>setNewConfirmUserPassword(e.target.value)} required/>
                <p>Password do not match</p>

                <Link to='/Main'>
                    <button onClick={changeUserData}>Submit changes</button>
                </Link>
            </form>
        </>
    )
}

export default ChangeUserData
