import React from "react";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import {deleteUser} from "../reducer/login";

const Main=()=>{

    const dispatch=useDispatch()
    const navigate=useNavigate()

    const [deleteConfirmation,setDeleteConfirmation]=useState(false)

    const username=useSelector((state)=>{
        return state.login.username
    })
    const userage=useSelector((state)=>{
        return state.login.userage
    })
    const usermail=useSelector((state)=>{
        return state.login.usermail
    })

    const deleteAccount=()=>{
        dispatch(deleteUser())
        navigate('/Login')
    }

    return(
        <>
            <div>MAIN</div>

            <div>nombre: {username}</div>
            <div>edad: {userage}</div>
            <div>email: {usermail}</div>

            <div>Change your data</div>
            <Link to='/Changeuserdata'>
                <button>Change data</button>
            </Link>

            <div>Delete account</div>

            {deleteConfirmation && (
                <div>
                <p>¿Estás seguro de que deseas borrar tu usuario?</p>
                <button onClick={()=>setDeleteConfirmation(true)}>Sí, borrar usuario</button>
                <button onClick={()=>setDeleteConfirmation(false)}>Cancelar</button>
                </div>
            )}
            <button onClick={deleteAccount}>Delete account</button>
        </>
    )
}

export default Main
