import React from "react";
import { useDispatch,useSelector } from "react-redux";

const ChangeUserData=()=>{
    const dispatch=useDispatch()
    const userName=useSelector((state)=>{
        return state.login.username
    })
    const userAge=useSelector((state)=>{
        return state.login.userage
    })
    const userMail=useSelector((state)=>{
        return state.login.usermail
    })

    return(
        <>
            <div>Change your information</div>
            <form>

                <label>Name</label>
                <input type="text" placeholder="Enter your name..." required>{userName}</input>

                <label>Age</label>
                <input type="number" placeholder="Enter your age..." required>{userAge}</input>

                <label>Email</label>
                <input type="email" placeholder="Enter your email..." required>{userMail}</input>

                <label>Confirm email</label>
                <input type="email" placeholder="Enter your email..." required>{userMail}</input>

                <label>Password</label>
                <input type="password" placeholder="Enter your password..." required/>
                <p>Password needs to be 8 characters or more</p>

                <label>Confirm password</label>
                <input type="password" placeholder="Enter your password..." required/>


                <button>Submit changes</button>
            </form>
        </>
    )
}

export default ChangeUserData
