import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import authService from "../Features/authService";

//Get user from localStorage
const user = JSON.parse(localStorage.getItem('user'))

//Register user
export const register = createAsyncThunk('userdata/register', async (user, thunkAPI)=>{
    try{
        return await authService.register(user)
    }catch(error){
        const message = (error.response && error.response.data && error.response.data.message) || error.message || error.toString()
        return thunkAPI.rejectWithValue(message)
    }
})

export const userDataSlice = createSlice({
    name:'userData',
    initialState:{
        user:user ? user : null,
        isError: false,
        isSuccess: false,
        isLoading:false,
        message:''
    },
    reducers:{
        newUser: (state,action)=>{
            state.username = action.payload.username
            state.userage=action.payload.userage
            state.usermail=action.payload.usermail
            state.userpassword=action.payload.userpassword
            state.validuser=true
        },
        changeUser: (state,action)=>{
            state.username = action.payload.username
            state.userage=action.payload.userage
            state.usermail=action.payload.usermail
            state.userpassword=action.payload.userpassword
            state.validuser=true
        },
        deleteUser: (state)=>{
            state.username=''
            state.userage=0
            state.usermail=''
            state.userpassword=''
            state.validuser=false
            state.user=null
        },
        reset: (state)=>{
            state.user = null
            state.isError=false
            state.isLoading=false
            state.isSuccess=false
            state.message=''
        }
    }
})

export const {newUser,changeUser,deleteUser,reset}=userDataSlice.actions

export default userDataSlice.reducer
